mapper.select:
SELECT t1.*, t3.name AS orgName, t3.uuid AS orgUuid FROM t1 LEFT JOIN t3 ON t3.uuid = t1.org_uuid
AND t1.username != #{superAdminUsername}
AND t1.org_uuid = (SELECT t4.org_uuid FROM t4 WHERE t4.uuid = #{userUuid})
AND (t1.username like #{user.username} or t1.realname like #{user.username} or t3.name like #{user.username})
ORDER BY id DESC
LIMIT #{start}, #{end}

SELECT t1.uuid, if(t1.realname = '' OR t1.realname is null, t1.username, t1.realname) realname, t3.name AS orgName FROM t1 LEFT JOIN t3 ON t3.uuid = t1.org_uuid LEFT JOIN (SELECT * FROM t_project_user WHERE project_uuid = #{projectUuid}) t4 ON t1.uuid = t4.user_uuid
AND t1.username != #{superAdminUsername}
AND (t3.name like #{user.realname} or t1.realname like #{user.realname})
AND t4.id is null
LIMIT #{start}, #{end}

mapper.update:
UPDATE t_user
SET update_time = NOW(), is_deleted = true, delete_version = id
WHERE uuid = #{uuid}

UPDATE t_user
SET update_time = NOW(), is_disabled = #{disabled}
WHERE uuid = #{uuid}

INSERT INTO t_person_login (accountName, password, salt)
VALUES (#{username}, #{password}, #{salt})
ON DUPLICATE KEY UPDATE
password = #{password},
salt = #{salt}

    , last_reset_pwd = #{lastResetPwd}

mapper.delete:
DELETE FROM t_user_role
WHERE user_uuid = #{userUuid} AND role_id IN

  #{item}

mapper.select:
SELECT accountName as username, last_reset_pwd
FROM t_person_login
WHERE accountName = #{username}

SELECT t1.*, t2.name AS orgName, t2.uuid AS orgUuid FROM t_user t1 LEFT JOIN t_org t2 ON t1.org_uuid = t2.uuid WHERE t1.org_uuid = #{orgUuid} AND !t1.is_deleted
AND (t1.username LIKE concat('%',#{keywords},'%') OR t1.realname LIKE concat('%',#{keywords},'%'))
LIMIT #{start}, #{end}

SELECT t2.*, t3.name AS orgName, t3.uuid AS orgUuid FROM t_user_role t1 LEFT JOIN t_user t2 ON t1.user_uuid = t2.uuid LEFT JOIN t_org t3 ON t2.org_uuid = t3.uuid WHERE t1.role_id = #{roleId} AND !t2.is_deleted
AND (t2.username LIKE concat('%',#{keywords},'%') OR t2.realname LIKE concat('%',#{keywords},'%') OR t3.name LIKE concat('%',#{keywords},'%'))
LIMIT #{start}, #{end}
